  import React, { Component } from "react";
  import logo from "./logo.svg";
  import "./App.css";
  import InfiniteScroll from "react-infinite-scroller";
  import Img from 'react-image'


var imageUrls = [];
var images = [];
  class Scroll2 extends Component {
    constructor(props) {
      super(props);
      this.state = {
        items: 13,
        hasMoreItems: true
      };

      for (var i = 58; i<=68;i++){
        imageUrls.push("IMG_22"+i+".jpg");
      }

    }

    showItems() {

      for (var i = 0; i < this.state.imageUrls; i++) {
      images.push(<img src={require(process.env.PUBLIC_URL + '/'+ imageUrls[i])} alt="logo"/>)
      //images.push(<Img src={require('../public/'+ imageUrls[i])} alt="logo"/>)
      console.log(process.env.PUBLIC_URL + '/'+ imageUrls[i]);
      }
      return images;
    }

    loadMore() {
      if(this.state.items===13){

        this.setState({ hasMoreItems: false});
      }else{
          setTimeout(() => {
          this.setState({ items: this.state.items + 5});
      }, 2000);
      }

    }

    render() {
      return (
        <div className="App">
          <div style={{height:'2000px', overflow:'auto'}}>
            <InfiniteScroll
              loadMore={this.loadMore.bind(this)}
              hasMore={this.state.hasMoreItems}
              loader={<div className="loader"> Loading... </div>}
              useWindow={true}
            >
              {this.showItems()}{" "}
            </InfiniteScroll>{" "}
          </div>{" "}
        </div>
      );
    }
  }

  export default Scroll2;
