import React, { Component } from "react";
    import logo from "./logo.svg";
    import "./App.css";

    import InfiniteScroll from "react-infinite-scroller";
    import firebase from 'firebase'
    // Initialize Firebase
  var config = {
    apiKey: "AIzaSyCFPcW9EdAcRq9syfi_-5yZwvUAfwI3PTE",
    authDomain: "busker-5561a.firebaseapp.com",
    databaseURL: "https://busker-5561a.firebaseio.com",
    projectId: "busker-5561a",
    storageBucket: "busker-5561a.appspot.com",
    messagingSenderId: "564719364967"
  };
  firebase.initializeApp(config);
  const itemsRef = firebase.database().ref('example');
  const storageRef = firebase.storage().ref()
    class Scroll2 extends Component {
      constructor(props) {
        super(props);
        this.state = {
          items: 20,
          hasMoreItems: true
        };
      }

      showItems() {
        var items = [];
        for (var i = 0; i < this.state.items; i++) {
          items.push(<li key={i}> Item {i} </li>);
        }
        return items;
      }

      loadMore() {
        if(this.state.items===50){

          this.setState({ hasMoreItems: false});
        }else{
            setTimeout(() => {
            this.setState({ items: this.state.items + 10});
        }, 2000);
        }

      }

      render() {
        return (
          <div className="App">
            <div style={{height:'1000px', overflow:'auto'}}>
              <InfiniteScroll
                loadMore={this.loadMore.bind(this)}
                hasMore={this.state.hasMoreItems}
                loader={<div className="loader"> Loading... </div>}
                useWindow={false}
              >
                {this.showItems()}{" "}
              </InfiniteScroll>{" "}
            </div>{" "}
          </div>
        );
      }
    }

    export default Scroll2;
