  //     width: Dimensions.get('window').width,
  //     height: Dimensions.get('window').width,
  //   },
  //   info: {
  //     flexDirection: 'row',
  //     justifyContent: 'space-between',
  //     padding: 10,
  //     paddingLeft: 15,
  //     paddingRight: 15,
  //     borderBottomWidth: 1,
  //     borderColor: '#d8d8d8',
  //   },
  //   infoText: {
  //     fontSize: 16,
  //     fontWeight: 'bold',
  //   },
  //   comment: {
  //     flexDirection: 'row',
  //     padding: 10,
  //     paddingLeft: 15,
  //     borderBottomWidth: 1,
  //     borderColor: '#d8d8d8',
  //   },
  //   commentText: {
  //     paddingRight: 15,
  //     fontWeight: 'bold',
  //   },
  // })
}

export default HomePage;
